import React , {Component} from "react"
import Cards from "./Cards"
import Categery from"./Categery"
import Welcome from"./Welcome"
import NavBar from"./NavBar"
import Pages from"./Pages"
import {getProducts, getCategery, deleteProduct} from "../FakeData/Products"

class Home extends Component{

    state={
        data:[]
        ,
        Categeries: getCategery()
        ,
        user:{
            Name:"",
            Email: "",
            Password: "",
            CreatedItems: []
        },
        selectedCategery: "All Categeries",
        currentPage : 1,
        pageSize: 4        
    }

   async componentDidMount(){
       const {data}= await getProducts()
        this.setState({data})
    }

    handleCategery=(Categery)=>{
        this.setState({selectedCategery: Categery})
        this.setState({currentPage: 1})
        
    }

    handleDeleteBtn= async(product)=>{
        const originalData = this.state.data
        try{
            await deleteProduct(product)
            window.location.reload()    
        }catch(ex){
            this.setState({data:originalData})
        }
    }

    handlePage=(page)=>{
        this.setState({currentPage: page})
    }

    render(){
        const {data, Categeries, selectedCategery, pageSize, currentPage, userData} = this.state 
        return(
            <React.Fragment>
                <Welcome/>
                <div className="container">
                    <Categery Categeries={Categeries} OnCategery={this.handleCategery} selectedCategery={selectedCategery}/>
                    <Cards onDelete={this.handleDeleteBtn} 
                    data={data} 
                    selectedCategery={selectedCategery}
                    pageSize={pageSize}
                    currentPage={currentPage}
                    onPage={this.handlePage}
                    user={user}
                    />
                </div>
            </React.Fragment>
        )
    }
}

export default Home;
