import React , {Component} from "react"
import Form from "../common/Form"
import Joi from "joi-browser"
import {getProducts, getCategery} from "../FakeData/Products"


class ProdustDetails extends Form{
    state={
        data: {_id:"",
                Name:"",
                Description:"",
                Price:"",
                Categery:"",
                SellingBy:"",
            },
            errors:{}
      }

schema={
    Name: Joi.string().min(4).max(125).label("Name"),
    Description:Joi.string().min(4).max(225).label("Description"),
    Price: Joi.number().integer().min(5).max(100000).label("Price"),
    Categery: Joi.string().max(20).label("Categery"),
    SellingBy: Joi.string().label("SellingBy"),
}

componentDidMount(){
    const _id = this.props.match.id
    const data = {...this.state.data}
    const product = getProducts().filter(product=> product._id === _id)
    data._id = product._id
    data.Name = product.Name
    data.Description = product.Description
    data.Categery = product.Categery
    data.SellingBy = product.SellingBy
    this.setState({data})
    console.log(data)
}


handleSave=(e)=>{
    e.preventDefault()
}


    render(){
        const {data, errors} = this.state
        return(
            <div className=" ml-3">
            <h1 className="mt-2 mb-5">Product</h1>
        <form onSubmit={this.handleSave}>
            {this.inputRender("Product name", "text", "Name", data.Name, errors)}
            {this.inputRender("Description", "text", "Description", data.Description, errors)}
            {this.inputRender("Price", "text", "Price", data.Price, errors)}
            {this.btnRender("Save")}
        </form>
        </div>
        )
    }
}

export default ProdustDetails;
