import axios from "axios";
import jwtDecode from "jwt-decode"
import http from "../Service/http"



export function userRegisteration(user){
return http.post("http://localhost:3000/api/users/signIn", {
    Name: user.Name,
    Email: user.Email,
    Password: user.Password
})
}

export function userLogin(Email, Password){
   return http.post("http://localhost:3000/api/users/logIn", {Email,Password})
}

export function getUser(){
    const jwt = localStorage.getItem("token")
    const user = jwtDecode(jwt)
    return user
}


export function getProfile(){
   return http.get("http://localhost:3000/api/users/me")
}

export function setJwt(jwt) {
    axios.defaults.headers.common["x-auth-token"] = jwt;
  }
