import React , {Component} from "react"
import Form from  "../common/Form"
import Joi from "joi-browser"
import {userLogin} from "../FakeData/Users"

class Login extends Form{
state={
    data:{Email:"", 
            Password: ""},

    errors:{}

}

schema = {
    Email:Joi.string().email().min(6).max(30).required(),
    Password:Joi.string().min(8).required(),
}



handleSubmit=async (e)=>{
e.preventDefault()
this.formValidation()
try{
    const user = await userLogin(this.state.data)
    this.setState({data:user})
    this.props.history.replace("/")
}catch(ex){
    console.log(ex)
}

}
    render(){
        const {data, errors} = this.state
        console.log(data)
        return(
            <div className=" ml-3">
                <h1 className="mt-2 mb-5">Log In</h1>
            <form onSubmit={this.handleSubmit}>
                {this.inputRender("Email", "text", "Email", data.Email, errors)}
                {this.inputRender("Password", "password", "Password", data.Password, errors)}
                {this.btnRender("Submit")}
            </form>
            </div>
        )
    }
}

export default Login;
