import React , {Component} from "react"
import Form from  "../common/Form"

class Login extends Form{
state={
    data:{username:"", password: ""}
}


schema = {
    Name:Joi.string().min(4).max(30),
    Email:Joi.string().email().min(6).max(30).required(),
    Password:Joi.string().min(8).required(),
}



handleSubmit=(e)=>{
e.preventDefault()
this.formValidation(this.schema)
console.log("submitted")
}
    render(){
        const {data} = this.state
        console.log(data)
        return(
            <div className=" ml-3">
                <h1 className="mt-2 mb-5">Log In</h1>
            <form>
                {this.inputRender("UserName", "text", "username", data.username)}
                {this.inputRender("Password", "password", "password", data.password)}
                {this.btnRender("Submit")}
            </form>
            </div>
        )
    }
}

export default Login;
