import React , {Component} from "react"
import Form from "../common/Form"
import Joi from "joi-browser"

class Register extends Form{
    state={
        data:{
            Name: "",
            Email: "",
            Password: "",
            CreatedItems:""
        },
        errors:{}
    }

schema = {
        Name:Joi.string().min(4).max(30),
        Email:Joi.string().email().min(6).max(30).required(),
        Password:Joi.string().min(8).required(),
}

formValidation=(e)=>{
    e.preventDefault();
    const validation = Joi.validate(this.state.data, this.schema)
    const errors = {...errors}
    if(validation.error){
       console.log(validation.error)
    }
}

    render(){
        const {data} = this.state
        return(
            <div className=" ml-3">
                <h1 className="mt-2 mb-5">Registeration</h1>
            <form >
                {this.inputRender("UserName", "text", "Name", data.Name)}
                {this.inputRender("Email", "email", "Email", data.Email)}
                {this.inputRender("Password", "password", "Password", data.Password)}
                {this.btnRender("Submit")}
            </form>
            </div>
        )
    }
}

export default Register;
