import React , {Component} from "react"
import Form from "../common/Form"
import Joi from "joi-browser"
import {userRegisteration} from "../FakeData/Users"

class Register extends Form{
    state={
        data:{
            Name: "",
            Email: "",
            Password: "",
//            CreatedItems:""
        },
        errors:{}
    }

schema = {
        Name:Joi.string().min(4).max(30).required().label("Name"),
        Email:Joi.string().email().min(6).max(30).required(),
        Password:Joi.string().min(8).required(),
}



handleSubmit=(e)=>{
    e.preventDefault()
    this.formValidation()
    userRegisteration(this.state.data)
    console.log("submitted")
}

    render(){
        const {data, errors} = this.state
        console.log(errors)
        return(
            <div className=" ml-3">
                <h1 className="mt-2 mb-5">Registeration</h1>
            <form onSubmit={this.handleSubmit}>
                {this.inputRender("UserName", "text", "Name", data.Name, errors)}
                {this.inputRender("Email", "email", "Email", data.Email, errors)}
                {this.inputRender("Password", "password", "Password", data.Password, errors)}
                {this.btnRender("Submit")}
            </form>
            </div>
        )
    }
}

export default Register;
