import React , {Component} from "react"
import Cards from "./Cards"
import Categery from"./Categery"
import Welcome from"./Welcome"
import NavBar from"./NavBar"
import {DeleteProduct, getProducts} from "../FakeData/Products"

class Home extends Component{

    state={
        data: getProducts()
        ,
        Categery:["Laptop", "Smart Phones","Accessories"]
        ,
        user:{
            Name:"",
            Email: "",
            Password: "",
            CreatedItems: []
        }


    }
    handleCategery=(Categery)=>{

    }

    handleDeleteBtn=(product)=>{
        const data = [...this.state.data]
        const index = data.indexOf(product)
        data.splice(index, 1)
        this.setState({data})
    }

    render(){
        const {data} = this.state 
        return(
            <React.Fragment>
                <NavBar/>
                <Welcome/>
                <div className="container">
                    <Categery Categery={this.state.Categery}/>
                    <Cards onDelete={this.handleDeleteBtn} products={data}/>
                </div>
            </React.Fragment>
        )
    }
}

export default Home;
