import React , {Component} from "react"
import {getProducts} from "../FakeData/Products"
import {pagination} from "../common/Pagination"
import Pages from "./Pages"
import {NavLink} from "react-router-dom"

class Card extends Component{
    
    render(){
        const{data, currentPage, pageSize, selectedCategery, onPage, user} =this.props
        let products;
        let selectedProducts;
        if(selectedCategery !== "All Categeries"){
          selectedProducts = data.filter(product=> product.Categery === selectedCategery)
        }else if(selectedCategery=== "All Categeries"){
          selectedProducts = data
        }
  
         products = pagination(selectedProducts, currentPage, pageSize)

        return(
          <React.Fragment>
          <div className="card-deck mb-3 text-center mt-4">
          {products.map(product=>
                <div key={product._id} className="card mb-4 shadow-sm">
            <div className="card-header">
              <h4 className="my-0 font-weight-normal"><NavLink to={`/Product/${product._id}`} >{product.Name}</NavLink></h4>
            </div>
            <div className="card-body">
              <h1 className="card-title pricing-card-title">{product.Price} <small className="text-muted">U$D</small></h1>
              <ul className="list-unstyled mt-0 mb-0">
                <li>{product.Description}</li>
                <li>Selling by: {product.SellingBy.Name}</li>
              </ul>
            {(user) ?  <button type="button" onClick={()=>this.props.onDelete(product)}className="btn btn-lg btn-block btn-outline-primary">Delete</button>:null}
            </div> }  
          </div>
                )}
            </div>
               <Pages data={data} 
                  pageSize={pageSize} 
                  currentPage={currentPage} 
                  onPage={this.handlePage}
                  productsLength={selectedProducts.length}
                  onPage={onPage} 
                  />

</React.Fragment>
        
            
        )
    }
}

export default Card;
