import React , {Component} from "react"
import Input from "./Input"
import SubmitBtn from "./SubmitBtn"
import Joi from "joi-browser"

class Form extends Component{
    state = {
        data:{},
        errors:{}

    }

    formValidation=(schema)=>{
        const validation = Joi.validate(this.state.data, schema)
        const errors={...errors}
        if(validation.error){
          errors[validation.error.details[0].path[0]] = validation.error.details[0].message
            this.setState({errors})
        }
    }

    handleChange= (e)=>{
        const data = {...this.state.data}
        const input = e.currentTarget
        data[input.name] = input.value
        this.setState({data})


        }


    inputRender=(label, type= "text", name, value)=>{
        return(
            <Input label={label}
            type={type}
            name={name}
            value={value}
            onChange={this.handleChange}/>
        )
    }    

    btnRender=(label)=>{
        return(
            <SubmitBtn label={label}/>
        )
    }

}
export default Form;
