import React , {Component} from "react"
import Cards from "./Cards"
import Categery from"./Categery"
import Welcome from"./Welcome"
import NavBar from"./NavBar"
import {DeleteProduct, getProducts, getCategery} from "../FakeData/Products"

class Home extends Component{

    state={
        data: getProducts()
        ,
        Categeries: getCategery()
        ,
        user:{
            Name:"",
            Email: "",
            Password: "",
            CreatedItems: []
        },
        selectedCategery: "All Categeries"
    }


    handleCategery=(Categery)=>{
        if(this.state.selectedCategery !== "All Categeries"){
          const data = this.state.data.filter(product=> product.Categery === Categery)
         this.setState({selectedCategery: Categery})
         this.setState({data})
        }else{
            this.setState({data:getProducts()})
        }
    }

    handleDeleteBtn=(product)=>{
        const data = [...this.state.data]
        const index = data.indexOf(product)
        data.splice(index, 1)
        this.setState({data})
    }

    render(){
        const {data, Categeries} = this.state 
        return(
            <React.Fragment>
                <NavBar/>
                <Welcome/>
                <div className="container">
                    <Categery Categeries={Categeries}/>
                    <Cards onDelete={this.handleDeleteBtn} products={data}/>
                </div>
            </React.Fragment>
        )
    }
}

export default Home;
